body.script:
"use strict";
function gbp(n){ return new Intl.NumberFormat('en-GB',{ style:'currency', currency:'GBP' }).format(Number(n||0)); }
function $(id){ return document.getElementById(id); }
const hmrcNo=$('hmrcNo'), invNo=$('invNo'), invDate=$('invDate'), dueDate=$('dueDate');
const clientName=$('clientName'), clientContact=$('clientContact'), billingAddr=$('billingAddr'), custType=$('custType'), vatNo=$('vatNo');
const device=$('device'), devType=$('devType'), devMake=$('devMake'), devModelTxt=$('devModelTxt');
const service=$('service'), desc=$('desc'), qty=$('qty'), price=$('price'), line=$('line'), list=$('list'), subtotal=$('subtotal'), grand=$('grand');

const SERVICES = {
  Repairs: ['Screen replacement','Battery replacement','Charging port repair','Water damage repair','Software issue','Diagnostic service','Board-level repair','Camera repair','Speaker/Mic repair','Back glass replacement','Data recovery'],
  Sales: ['Tempered glass protector','Phone case','Charging cable','Wall charger','Power bank','Earphones/Headset','USB-C adapter','Lightning cable','Wireless charger','Laptop charger','Mouse/Keyboard','Memory card','USB drive','HDMI cable','Screen cleaner kit','Refurbished phone','Refurbished tablet','Refurbished laptop']
};
const DEV_TYPES=['Phone','Tablet','Laptop','Desktop','Console','Watch','Other'];
const DEV_MAKES=['Apple','Samsung','Google','Huawei','OnePlus','Xiaomi','Sony','Nokia','Motorola','Dell','HP','Lenovo','Asus','Acer','Microsoft','Nintendo','Other'];

let items=[];
document.addEventListener('DOMContentLoaded', init);

function init(){
  const now=new Date(); const pad=n=>String(n).padStart(2,'0');
  invDate.value = now.toISOString().split('T')[0];
  const d=new Date(now); d.setDate(d.getDate()+30); dueDate.value = d.toISOString().split('T')[0];

  // HMRC sequential number (gap-free within this browser profile)
  let seq = Number(localStorage.getItem('hmrc_seq')||0)+1;
  localStorage.setItem('hmrc_seq', seq);
  hmrcNo.value = now.getFullYear()+"-"+String(seq).padStart(4,'0');

  // Legacy number
  const lseq = Number(localStorage.getItem('legacy_seq')||0)+1;
  localStorage.setItem('legacy_seq', lseq);
  invNo.value = now.getFullYear()+"-"+pad(now.getMonth()+1)+pad(now.getDate())+"-"+String(lseq).padStart(4,'0');

  DEV_TYPES.forEach(t=>{ const o=document.createElement('option'); o.value=t; o.textContent=t; devType.appendChild(o); });
  DEV_MAKES.forEach(m=>{ const o=document.createElement('option'); o.value=m; o.textContent=m; devMake.appendChild(o); });

  service.innerHTML = 'Select';
  Object.keys(SERVICES).forEach(group=>{
    const og=document.createElement('optgroup'); og.label=group;
    SERVICES[group].forEach(name=>{ const o=document.createElement('option'); o.value=name; o.textContent=name; og.appendChild(o); });
    service.appendChild(og);
  });

  devType.addEventListener('change', updateDevice);
  devMake.addEventListener('change', updateDevice);
  devModelTxt.addEventListener('input', updateDevice);

  qty.addEventListener('input', recalc);
  price.addEventListener('input', recalc);
  recalc();

  service.addEventListener('change', ()=>{ if(!desc.value) desc.value=service.value; });

  $('add').addEventListener('click', ()=>{
    const q=Number(qty.value||0), p=Number(price.value||0); if(q<=0) return;
    items.push({desc:desc.value||service.value||'Item', qty:q, price:p});
    desc.value=''; qty.value=1; price.value=0; recalc(); render();
  });
  $('clear').addEventListener('click', ()=>{ items=[]; render(); });

  $('print').addEventListener('click', ()=>window.print());
  $('export').addEventListener('click', ()=>{
    const blob=new Blob([JSON.stringify(collect(),null,2)],{type:'application/json'});
    const a=document.createElement('a'); a.href=URL.createObjectURL(blob); a.download=(invNo.value||'invoice')+'.json'; a.click();
  });
  $('save').addEventListener('click', ()=>{
    localStorage.setItem('btl_invoice_saved', JSON.stringify(collect()));
    alert('Saved');
  });
  $('load').addEventListener('click', ()=>{
    const raw=localStorage.getItem('btl_invoice_saved'); if(!raw) return alert('Nothing saved');
    try{
      const d=JSON.parse(raw);
      hmrcNo.value=d.hmrcNo||hmrcNo.value; invNo.value=d.invNo||invNo.value;
      invDate.value=d.invDate||invDate.value; dueDate.value=d.dueDate||dueDate.value;
      clientName.value=d.clientName||''; clientContact.value=d.clientContact||'';
      billingAddr.value=d.billingAddr||''; custType.value=d.custType||'Individual'; vatNo.value=d.vatNo||'';
      device.value=d.device||''; service.value=d.service||'';
      items=Array.isArray(d.items)?d.items:[]; render();
    }catch{ alert('Load failed'); }
  });

  render();
}

function collect(){
  return {
    hmrcNo:hmrcNo.value, invNo:invNo.value,
    invDate:invDate.value, dueDate:dueDate.value,
    customer:{ name:clientName.value, contact:clientContact.value, type:custType.value, vat:vatNo.value, billing:billingAddr.value },
    device:device.value, service:service.value,
    items:items, totals:{ subtotal:subtotal.textContent, total:grand.textContent }
  };
}

function updateDevice(){ device.value=[devType.value, devMake.value, devModelTxt.value].filter(Boolean).join(' '); }
function recalc(){ line.value = gbp((Number(qty.value||0)*Number(price.value||0))); }

function render(){
  list.innerHTML=''; let sub=0;
  items.forEach((it,i)=>{
    const total = it.qty*it.price; sub+=total;
    const row=document.createElement('div'); row.className='row';
    row.innerHTML=`




      Remove`;
    const ins=row.querySelectorAll('input'); const rm=row.querySelector('button');
    ins[0].addEventListener('input', e=>{ it.desc=e.target.value; });
    ins[1].addEventListener('input', e=>{ it.qty=Number(e.target.value||0); updateTotals(); });
    ins[2].addEventListener('input', e=>{ it.price=Number(e.target.value||0); updateTotals(); });
    rm.addEventListener('click', ()=>{ items.splice(i,1); render(); });
    list.appendChild(row);
  });
  subtotal.textContent=gbp(sub); grand.textContent=gbp(sub);
}

function updateTotals(){
  let sub=0; const rows=list.querySelectorAll('.row');
  items.forEach((it,idx)=>{ const t=it.qty*it.price; sub+=t; const out=rows[idx].querySelectorAll('input')[3]; if(out) out.value=gbp(t); });
  subtotal.textContent=gbp(sub); grand.textContent=gbp(sub);
}
